public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        // Reading input from stdin
        int a = sc.nextInt();
        int b = sc.nextInt();
        // Output the sum of a and b
        System.out.println(a + b);
        sc.close(); // Close the scanner to prevent resource leaks
    }
}

